CREATE TABLE life_table (
    period        TEXT NOT NULL,
    age           SMALLINT NOT NULL,
    total_living  INT,
    male_living   INT,
    female_living INT,
    total_died    INT,
    male_died     INT,
    female_died   INT,
    total_qx      NUMERIC,
    male_qx       NUMERIC,
    female_qx     NUMERIC,
    PRIMARY KEY(period, age)
);

COMMENT ON COLUMN life_table.total_living IS 'Total people still alive at this age';
COMMENT ON COLUMN life_table.total_died IS 'Total people who died before next birthday';
COMMENT ON COLUMN life_table.total_qx IS 'qx is likelihood of dying before next birthday';

